uint64_t pow7(unsigned int k, unsigned int n)
{
  uint64_t r = 1;
  while (n) {
    if (n &amp; 1) { // n % 2
      r *= k;
    }
    k *= k;
    n &gt;&gt;= 1; // n = n / 2;
  }

  return r;
}



